T.Fail(function() rbxmk.runFile(42) end                                          , "runFile expects string for its first argument")
T.Fail(function() rbxmk.runFile("nonextant.lua") end                             , "runFile throws an error when file does not exist")
T.Pass(rbxmk.runFile(path.expand("$sd/_average.lua"),1,2,3,4,5,6) == 3.5           , "runFile receives arguments and returns results")
T.Fail(function() rbxmk.runFile(path.expand("$sd/_average.lua"),1,2,3,true,5,6) end, "runFile throws an error when script errors")
T.Fail(function() rbxmk.runFile(path.expand("$sd/$sn")) end                        , "runFile throws an error when script is already running")
T.Fail(function() rbxmk.runFile(path.expand("$sd/_badsyntax.lua")) end             , "runFile throws an error when script could not be loaded")
