local desc = fs.read(os.expand("$sd/../../dump.desc.json"))
local instance = Instance.new("BoolValue")
T.Pass(instance[sym.RawDesc] == nil                   , "initializes with nil")
T.Pass(function() instance[sym.RawDesc] = desc end    , "can be set to RootDesc")
T.Pass(instance[sym.RawDesc] == desc                  , "returns RootDesc after being set to RootDesc")
T.Pass(function() instance[sym.RawDesc] = nil end     , "can be set to nil")
T.Pass(instance[sym.RawDesc] == nil                   , "returns nil after being set to nil")
T.Pass(function() instance[sym.RawDesc] = false end   , "can be set to false")
T.Fail(function() instance[sym.RawDesc] = true end    , "cannot be set to true")
T.Pass(instance[sym.RawDesc] == false                 , "returns false after being set to false")
T.Fail(function() instance[sym.RawDesc] = "foobar" end, "errors without RootDesc, false, or nil")
T.Pass(function() instance[sym.Desc] = desc end       , "setting Desc to RootDesc sets RawDesc")
T.Pass(instance[sym.RawDesc] == desc                  , "returns RootDesc after Desc is set to RootDesc")
T.Pass(function() instance[sym.Desc] = nil end        , "setting Desc to nil sets RawDesc")
T.Pass(instance[sym.RawDesc] == nil                   , "returns nil after Desc is set to nil")
T.Pass(function() instance[sym.Desc] = false end      , "setting Desc to false sets RawDesc")
T.Pass(instance[sym.RawDesc] == false                 , "returns false after Desc is set to false")
